    private void calcular() {
        
        Double altura = Double.parseDouble(txtAltura.getText());
        Double raio = Double.parseDouble(txtRaio.getText());
        
        lblVolume.setText("O Volume Ã©: "+(3.14159 * Math.pow(raio, 2) * altura));
    }
